import { useRouteLoaderData, redirect } from "react-router-dom";
import EventItem from "../components/EventItem";

function EventDetailPage() {
    const data = useRouteLoaderData('event-detail');

    return (
        <>
            <EventItem event={data.event} />
            event detail page
        </>
    );
}

export default EventDetailPage;

export async function loader({ request, params }) {

    const id = params.eventId;

    const response = await fetch('http://localhost:8081/events/' + id);
    if (!response.ok) {
        throw new Response(JSON.stringify({ message: "Could not fetch details for selected event." }), { status: 500 })
    } else {
        return response;
    }
}

export async function action({ params }) {
    const eventId = params.eventId;
    const response = await fetch('http://localhost:8081/events/' + eventId);

    if (!response.ok) {
        throw new Response(JSON.stringify({ message: "could not delete event..." }), { status: 500 });
    }

    return redirect('/events');
}
